class Solution(object):
    def longestConsecutive(self, root):
        def helper(root):
            if not root:
                return 0, 0
            left_len = helper(root.left)
            right_len = helper(root.right)
            cur_inc_len, cur_dec_len = 1, 1
            if root.left:
                if root.left.val == root.val + 1:
                    cur_inc_len = max(cur_inc_len, left_len[0] + 1)
                elif root.left.val == root.val - 1:
                    cur_dec_len = max(cur_dec_len, left_len[1] + 1)
            if root.right:
                if root.right.val == root.val + 1:
                    cur_inc_len = max(cur_inc_len, right_len[0] + 1)
                elif root.right.val == root.val - 1:
                    cur_dec_len = max(cur_dec_len, right_len[1] + 1)
            self.max_len = max(self.max_len, cur_dec_len + cur_inc_len - 1)
            return cur_inc_len, cur_dec_len

        self.max_len = 0
        helper(root)
        return self.max_len

class Solution(object):
    def splitLoopedString(self, strs):
        tmp = []
        for s in strs:
            tmp += max(s, s[::-1])
        s = "".join(tmp)

        result, st = "a", 0
        for i in xrange(len(strs)):
            body = "".join([s[st + len(strs[i]):], s[0:st]])
            for p in strs[i], strs[i][::-1]:
                for j in xrange(len(strs[i])):
                    if p[j] >= result[0]:
                        result = max(result, "".join([p[j:], body, p[:j]]))
            st += len(strs[i])
        return result

class Solution:
    def nextClosestTime(self, time: str) -> str:
        time = time[0:2]+time[3:]

        def find_larger(temp, comp):
            for i in sorted(time):
                if i < temp[0]: continue
                for j in sorted(time):
                    if i+j > temp and i+j < comp:
                        return i+j
            return "99"

        new_min = find_larger(time[2:], "60")
        if new_min != "99":
            return time[0:2] + ":" + new_min

        new_hour = find_larger(time[:2], "24")
        dig = min(time)
        if new_hour != "99":
            return new_hour + ":" + dig + dig
        return dig + dig + ":" + dig + dig

